import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Link, useParams } from 'react-router-dom';

export default function ViewStudent() {
    const [student, setStudent]=useState({
        name:"",
        username:"",
        email:""
    })

    const {id}=useParams();
    useEffect(()=>{
        loadStudent()



    },[])
    const loadStudent=async()=>{
        const result=await axios.get(`http://loacalhost:8080/student/${id}`)
        setStudent(result.data)
    }
  return (
    <div className="container">
        <div className="row">
            <div className="col-md-6 offset-md-3 borer rounded p-4 mt-2 shadow">
                <h2 className="text-center m-4">Student Details</h2>

                <div className="card">
                    <div className="card-header">
                        Details of Student id:{student.id}
                        <ul className="list-group list-group-flush">
                            <li className="list-group-item">
                                <b>Name:</b>
                                {student.name}
                            </li>
                            <li className="list-group-item">
                                <b>Userame:</b>
                                {student.username}
                            </li>
                            <li className="list-group-item">
                                <b>Email:</b>
                                {student.email}
                            </li>
                        </ul>
                    </div>
                </div>
                   <Link classNAme="btn btn-primary my-2" to={"/"}>
                    Back to Home
                   </Link>
                
                </div>
         </div>
    </div>
  );
}
